const themePalette ={
    grey:'#545454',
    yellow:'#F5C121',
    white: '#ffffff',
    darkyellow:'#b08500',
    lightgrey:'#a6a6a6',
    font1: 'Questrial_400Regular',
    font2: 'Sriracha_400Regular'
}

export const HwangsTheme = {
    
    
    Text: {
        style:{
            fontFamily: 'Questrial_400Regular',
            color: themePalette.grey,
            lineHeight:28
        },
        h1Style: {
            color: themePalette.grey,
            fontSize: 26,
            fontFamily: 'Questrial_400Regular',
            lineHeight: 45,
            textAlign: 'right'
        },
        h2Style: {
            color: themePalette.yellow,
            fontSize: 20,
            margin:10
        },
        
    }

};
