import { StyleSheet, View, FlatList, } from 'react-native';

import { ThemeProvider, Text, Button} from 'react-native-elements';

import { HwangsTheme } from '../themes/HwangsTheme';

import FoodItem from '../components/FoodItem';
import { getAllFoods } from '../data/FOODDATA';


export default function Menu({ navigation }) {

  const renderItem = ({ item }) => (
    <FoodItem itemData={item} navigatorRef={navigation} />
  );
  return (
    <ThemeProvider theme={HwangsTheme}>
      <View style={styles.container}> 
          <View style={styles.secondHeader}>
          <Button 
            style={{
              padding:10,
              border:0,
              height:30,
            }}
            icon={{
              name: 'arrow-back-outline',
              type: 'ionicon',
              size: 20,
            }} 
            onPress={() => navigation.goBack()} />
            <Text h1>Menu</Text>
            </View>
        <FlatList
          style={styles.foodList, {backgroundColor:'#fff'}}
          data={getAllFoods()}
          renderItem={renderItem}
          keyExtractor={item => item.id}
          keyImage='true'
        />

      </View>
    </ThemeProvider>

  );

}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      paddingTop: 50
    },
    secondHeader: {
      flexDirection: 'row',
      width: '100%'
    }
  
  });
