import { Button, Text, ListItem, ThemeProvider } from 'react-native-elements';
import { Image} from 'react-native';
import '../css/main.css';

import { HwangsTheme } from '../themes/HwangsTheme';



export default function MyListItem({ itemData, navigatorRef }) {

  return (    
    <ThemeProvider theme={HwangsTheme}>
    <ListItem>

      <ListItem.Content >
          <Image source={require('./img/food/${this.props.itemData.id}.png')} />
        <Text>{itemData.name}</Text>
      </ListItem.Content>

      <Button
        icon={{
          name: 'add-outline',
          type: 'ionicon',
          size: 20,
        }}
        iconPosition='center'
        disabled={!itemData.active}
        onPress={() => navigatorRef.navigate('ResortDetail', {
          detailId: itemData.id,
        })}
      />
    </ListItem>
    </ThemeProvider>
  );
}
