import React, { useState, useContext } from "react";


import {  Text, ListItem, ThemeProvider, Avatar } from 'react-native-elements';
import { CartDispatchContext, addToCart } from "../contexts/cart";

import '../css/main.css';

import { HwangsTheme } from '../themes/HwangsTheme';

const ProductCard = ({ itemData }) => {
  const [isAdded, setIsAdded] = useState(false);
  const dispatch = useContext(CartDispatchContext);
  const { keyImage, name, price, id } = itemData;

  quantityNumber=0; 

  const handleAddToCart = () => {
    const product = { ...itemData, quantity: 1 };

    addToCart(dispatch, product);
    setIsAdded(true);
    setTimeout(() => {
      setIsAdded(false);
    }, 3500);
  quantityNumber=quantityNumber+1;
  console.log({quantityNumber});



  };


  return (    
    <ThemeProvider theme={HwangsTheme}>
    <ListItem>
      <ListItem.Content>
      <Avatar source={{uri: itemData.keyImage}} style={{ width: 200, height: 200, alignSelf:'center'}} />
        <Text style={{alignSelf:'center'}}>{itemData.name}</Text>
        <Text style={{alignSelf:'center'}}>{itemData.price}</Text>
      </ListItem.Content>
    </ListItem>
    <button
        className={!isAdded ? "" : "added"}
        onClick={handleAddToCart}
        style={{
          alignSelf:'center',
          marginBottom: 20
        }}
      >
          {!isAdded ? "ADD TO ORDER" : "✔ ADDED"}

        </button>
    </ThemeProvider>
  );
};
export default ProductCard;
