import * as React from "react";

// Import use state
import { useState } from "react";
import { StyleSheet, View, } from 'react-native';

// Import theme
import { ThemeProvider, Text, } from 'react-native-elements';
import { HwangsTheme } from '../themes/HwangsTheme';

export default function Header() {
  const [navbarOpen, setNavbarOpen] = useState(false);

  const handleToggle = () => {
    setNavbarOpen(!navbarOpen)
  };
  return (
    <ThemeProvider theme={HwangsTheme}>
      <View style={styles.container}> 

          <View style={{ flex: 1 }}>
            <View>
              <nav style={styles.navBar}>
                <button style={styles.hamburger} onClick={handleToggle}>{navbarOpen ? "Close" : "Open"}</button>
                <ul style={styles.menuNav} className={`${navbarOpen ? " showMenu" : ""}`}>
                  <li>
                    <a href="#">Menu</a>
                    <a href="#">Catering</a>
                    <a href="#">About</a>
                  </li>
                </ul>
              </nav>
            </View>


            <View style={{ flex: 1 }}>
              <Text h1>Welcome to {'\n'}Resorts R'Us</Text>              
            </View>

          </View>

      </View>
    </ThemeProvider>

  );

}


const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    navBar: {
      position: relative,
    },
    
    hamburger: {
      position: fixed,
      left: 40,
      top: 40,
      zIndex: 10,
      cursor: pointer,
    },
    
    menuNav: {
      overflowY: {scroll},
      listStyle: none,
      position: fixed,
      top: 0,
      left: 0,
      bottom: 0,
      height: '100vh',
      width: 0,
      overflow: hidden,
      maxWidth: 290,
      zIndex: 9,
    },
    
    
  
  });
