import { StyleSheet, View} from 'react-native';

import { ThemeProvider, Text, Button} from 'react-native-elements';

import '../css/main.css';

import { HwangsTheme } from '../themes/HwangsTheme';

// Import images
import storefront from '../assets/img/storefront.jpg';

export default function HomeScreen({ navigation }) {

  return (
    <ThemeProvider theme={HwangsTheme}>
      <View style={styles.container}> 

       

        <View style={styles.container, {padding:40}, {backgroundColor:'#545454'}}>
          <Text style={{fontSize:20}}>Located in the Henderson Mall {"\n"}food-court (2nd floor)</Text>

          <View>
            <div style={{flex:1}}>
              <div className='box'></div>
              <img src={storefront} alt="Hwang's Kitchen storefront" className='storefrontimg' />
            </div>
            <Text>2088-1163 Pinetree Way, {"\n"}Coquitlam, BC, V3B 8A6</Text>
          </View> 

        </View>

        

      </View>
      
    </ThemeProvider>

  );

}

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },

    bannerDisplay: {
      flexDirection:'row',
      flexWrap: 'wrap',
      padding: 40

    },
    bannerTxt: {
      flex: '1 2 50%',
      marginTop: 30
    },
    orderCTA:{
      alignSelf: 'flex-start',
    }
  
  });
