const themePalette ={
    grey:'#545454',
    yellow:'#F5C121',
    white: '#ffffff',
    darkyellow:'#b08500',
    lightgrey:'#a6a6a6',
}

export const HwangsTheme = {
    
    Button: {
        buttonStyle: {
            raised: true,
            borderRadius: 40,
        },
        titleStyle: {
            color: themePalette.grey,
        },
        disabledStyle:{
            backgroundColor:themePalette.lightgrey,
        },
        type: 'outline'
    },

    Text: {
        style:{
            fontFamily: themePalette.font1,
            color: themePalette.grey
        },
        h1Style: {
            color: themePalette.grey,
            fontSize: 24,
            fontFamily: themePalette.font1,
        },
        h2Style: {
            fontFamily: themePalette.font1,
            color: themePalette.white,
            fontSize: 22,
            margin:10
        },
    }

};
