import * as React from 'react';
import { StyleSheet, View, ActivityIndicator, ScrollView } from 'react-native';

import { SafeAreaProvider } from 'react-native-safe-area-context';

import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

import { useFonts } from 'expo-font';
import { Sriracha_400Regular } from '@expo-google-fonts/sriracha';
import { Questrial_400Regular } from '@expo-google-fonts/questrial';



import HomeScreen from './screens/HomeScreen';
import Cart from './screens/Cart';
import Menu from './screens/Menu';
import Header from './components/Header';

import Main from './components/Main';
import Basket from './components/Basket';
import FOODDATA from './data/FOODDATA';
import Footer from './components/Footer';


export default function App() {

  const { FOODDATA } = data;
  const [cartItems, setCartItems] = useState([]);
  const onAdd = (FOODDATA) => {
    const exist = cartItems.find((x) => x.id === FOODDATA.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === FOODDATA.id ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...FOODDATA, qty: 1 }]);
    }
  };
  const onRemove = (FOODDATA) => {
    const exist = cartItems.find((x) => x.id === FOODDATA.id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== FOODDATA.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === FOODDATA.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };

  let [fontsLoaded] = useFonts({ 
    Sriracha_400Regular, 
    Questrial_400Regular,
  });

  if (!fontsLoaded) { 
    return ( 
      <View style={styles.loadingContainer}> 
        <ActivityIndicator size='large' color='#F5C121' /> 
      </View> 
    ); 
  }
const Stack = createNativeStackNavigator(
);

  return (
    <SafeAreaProvider>
        <div className="App">
          <Header countCartItems={cartItems.length} />
            <div className="row">
              <Main FOODDATA={FOODDATA} onAdd={onAdd}></Main>
              <Basket
              cartItems={cartItems}
              onAdd={onAdd}
              onRemove={onRemove}
              ></Basket>
            </div>
        </div>
        <NavigationContainer>
          <Stack.Navigator initialRouteName="Home">
            <Stack.Screen
              name="Home"
              component={HomeScreen}
              options={{ headerShown: false }}
            />
            <Stack.Screen
              name="Menu"
              component={Menu}
              options={{ title: 'Menu' }}
            />
            <Stack.Screen
              name="Cart"
              component={Cart}
              options={{ title: 'Cart' }}
            />
          </Stack.Navigator>
        </NavigationContainer>
    </SafeAreaProvider>


  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'stretch',
    justifyContent: 'flex-start',
    width: '414px',
    height: '896px'
  },


});
