import { StyleSheet, View} from 'react-native';

import { ThemeProvider, Text, } from 'react-native-elements';

import '../css/main.css';

import { HwangsTheme } from '../themes/HwangsTheme';

// Import images
import banner from '../assets/img/bannerimg.jpg'
import storefront from '../assets/img/storefront.jpg';

export default function HomeScreen({ navigation }) {

  return (
    <ThemeProvider theme={HwangsTheme}>
      <View style={styles.container}> 
          <View style={styles.bannerDisplay}>
              <div style={{flex:1}}>
                <div className='box'></div>
                <img src={banner} alt="Bibimbap" className='bannerimg' />
              </div> 
              <Text h1 style={styles.bannerTxt}>authentic and {"\n"}<span style={{color: '#F5C121'}}>home-cooked Korean meals</span></Text>
          </View>
      </View>
      
    </ThemeProvider>

  );

}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "white",
    },

    bannerDisplay: {
      flex:1,
      flexDirection:'row',
      justifyContent:'space-between',
      padding: 40

    },
    bannerTxt: {
      flex: 1
    }
  
  });
