import * as React from "react";

// Import use state
import { useState } from "react";

import '../css/main.css';
import { StyleSheet, View, } from 'react-native';

// Import theme
import { ThemeProvider, } from 'react-native-elements';
import { HwangsTheme } from '../themes/HwangsTheme';

// Import images
import logo from '../assets/img/logo.png';
import cart from '../assets/img/cart.png';

export default function Header() {
  const [navbarOpen, setNavbarOpen] = useState(false);

  const handleToggle = () => {
    setNavbarOpen(!navbarOpen)
  };
  return (
    <ThemeProvider theme={HwangsTheme}>
      <View style={styles.container}> 
            <View style={{ alignSelf: "flex-start", width: 27, flex:1 }}>
              <nav className="navBar">
                <button onClick={handleToggle}>
                  <div className={navbarOpen ? "open" : "close"}>
                                    <span></span>
                                    <span></span>
                                    <span></span>
                  </div>
                  </button>
                <ul className={`menuNav ${navbarOpen ? "showMenu" : ""}`}>
                  <li>
                    <a href="#">Menu</a>
                  </li>
                  <li>
                    <a href="#">Catering</a>
                  </li>
                  <li>
                    <a href="#">About</a>
                  </li>
                </ul>
              </nav>
            </View>
            <View style={{ alignSelf: "center", flex:1}}>
              <a href="../screens/HomeScreen">
                <img src={logo} alt="Hwang's Kitchen" className="logo" />
              </a>         
            </View>
            <View style={{ alignSelf: "flex-end", flex:1}}>
              <img src={cart} alt="Shopping cart icon" className="cart" />
            </View>
      </View>
    </ThemeProvider>

  );

}


const styles = StyleSheet.create({
    container: {
      top:0,
      flex:1,
      
    }
  });
