import React, { useState, useContext } from "react";

import { Button, Text, ListItem, ThemeProvider, Avatar } from 'react-native-elements';
import { CartDispatchContext, addToCart } from "contexts/cart";

import '../css/main.css';

import { HwangsTheme } from '../themes/HwangsTheme';



export default function MyListItem({ itemData, navigatorRef }) {

  return (    
    <ThemeProvider theme={HwangsTheme}>
    <ListItem>
      <ListItem.Content>
      <Avatar source={{uri: itemData.keyImage}} style={{ width: 200, height: 200, alignSelf:'center'}} />
        <Text style={{alignSelf:'center'}}>{itemData.name}</Text>
        <Text style={{alignSelf:'center'}}>{itemData.price}</Text>
      </ListItem.Content>
    </ListItem>
    <Button
        style={{
          alignSelf:'center',
          marginBottom: 20
        }}
        icon={{
          name: 'add-outline',
          type: 'ionicon',
          size: 20,
        }}
        iconPosition='center'
        disabled={!itemData.active}
        onPress={() => navigatorRef.navigate('ResortDetail', {
          detailId: itemData.id,
        })}
      />
    </ThemeProvider>
  );
}
