import { StyleSheet, View, } from 'react-native';

import { ThemeProvider, Button, Text, } from 'react-native-elements';

import { hwangsTheme } from '../themes/HwangsTheme';

export default function HomeScreen({ navigation }) {

  return (
    <ThemeProvider theme={hwangsTheme}>
      <View style={styles.container}> 
        <ImageBackground
          style={styles.backgroundImage}
          source={require('../assets/lab-assets/beach-splash-bg.png')}>

          <View style={{ flex: 1 }}>
            <View style={{ flex: 2 }}>
              <Text h1 style={styles.headingText}>Welcome to {'\n'}Resorts R'Us</Text>              
            </View>

            <View style={{ flex: 1, paddingHorizontal: 10 }}>
              <Button
                title="Browse Resorts"
                icon={{
                  name: 'arrow-forward-outline',
                  type: 'ionicon',
                  size: 25,
                }}
                iconPosition='right'
                raised={true}
                onPress={() => navigation.navigate('ResortList')}
              />
            </View>

          </View>

        </ImageBackground>
      </View>
    </ThemeProvider>

  );

}

const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
  
  });
